bvec1<-lapply(data,corbsub,x[,1],x[,2],corfun) 
bvec2<-lapply(data,corbsub,y[,1],y[,2],corfun) 
}
mat1=matl(bvec1)
mat2=matl(bvec2)
ihi<-floor((1-alpha/2)*nboot+.5)
ilow<-floor((alpha/2)*nboot+.5)
