abline(reg1)
abline(reg2,lty=2)
}
if(plotPV){
plot(pts,pv,xlab=xlab,ylab=ylab,pch=pch)
}
list(output=est,p.crit=p.crit,crit.value=crit,num.sig=sum(est[,5]<=p.crit))
}

