bdmP<-function(x){
#
# Test the null hypothesis in Bruner et al. (2016) 
# for a one-way design
#
if(is.matrix(x))x=listm(x)
J=length(x)
library(rankFD)
nv=lapply(x,length)
nv=as.vector(matl(nv))
z=x[[1]]
g=rep(1,nv[1])
