mp
}
ols.ridge<-function(x,y,k=NULL,xout=FALSE,outfun=outpro){
library(MASS)
x=as.matrix(x)
if(ncol(x)==1)stop('Should have two or more independent variables.')
xy=elimna(cbind(x,y))
x=as.matrix(x)
p=ncol(x)
p1=p+1
x=xy[,1:p]
y=xy[,p1]
x=as.matrix(x)
if(xout){
flag<-outfun(x)$keep
x<-x[flag,]
x<-as.matrix(x)
