lambda.lasso.opt = model.est$lambda, residuals = Y.res)                                    
}                                                                                         
                                                                                          
winsorized<- function(x,a=1.5,sigma=1) {                                                  
s<-sigma                                                                                  
newx<-x                                                                                   
indp<-x>(a*s)                                                                             
newx[indp]<-(a*s)                                                                         
indn<- x<(a*-s)                                                                           
newx[indn]<- (-a*s)                                                                       
newx                                                                                      
}          

