up=nboot-low
for (j in 1:J){
for (jj in 1:J){
if (j < jj){
for (k in 1:K){
for (kk in 1:K){
if (k < kk){
jcom<-jcom+1
test[jcom,1]<-j
test[jcom,2]<-jj
test[jcom,3]<-k
test[jcom,4]<-kk
d=NA
for(b in 1:nboot){
n1=nrow(x[[mat[j,k]]])
n2=nrow(x[[mat[j,kk]]])
n3=nrow(x[[mat[jj,k]]])
n4=nrow(x[[mat[jj,kk]]])
X1=x[[mat[j,k]]]
X2=x[[mat[j,kk]]]
X3=x[[mat[jj,k]]]
X4=x[[mat[jj,kk]]]
id1=sample(n1,n1,replace=TRUE)
