lplot(x,res,ZLIM=ZLIM,span=span,scale=scale,xlab=xlab,ylab=ylab,zlab,zlab,ticktype=ticktype,frame=frame,theta=theta,
phi=phi,pyhat=pyhat,eout=eout,xout=FALSE)
