pt=as.vector(pt)
x=elimna(x)
n=nrow(pts)
if(K>nrow(x))stop(' Cannot have K>n')                                                                            
dis=sqrt(mahalanobis(x,t(pt),mcov))                                                               
chk.dup=sum(duplicated(dis))                                                                      
if(chk.dup>0)dis=jitter(dis)                                                                      
ord=sort(dis)                                                                                     
id=which(dis<=ord[K])                                                                                                                                                                        
id                                                                                                
}                                                                                                 
                                                                                                  
