rhohc4bt<-function(X1,Y1,alpha=.05,nboot=2999,SEED=TRUE){
#
# A confidence interval for 
# Pearson's correlations using a bootstrap-t method in conjunction with the HC4 estimator
#
if(SEED)set.seed(2)
XY=cbind(X1,Y1)
XY=elimna(XY)
X1=XY[,1]
Y1=XY[,2]
r1=cor(X1,Y1)
n1=length(X1)
v=NA
Nboot=nboot+1
for(i in 1:Nboot){
if(i<=nboot)id1=sample(n1,n1,replace=TRUE)
if(i==Nboot)id1=c(1:n1)

x1=X1[id1]
y1=Y1[id1]
x1=(x1-mean(x1))/sd(x1)
y1=(y1-mean(y1))/sd(y1)
temp1=olshc4(x1,y1)
if(i<=nboot)v[i]=(temp1$ci[2,2]-r1)/temp1$ci[2,6]
if(i==Nboot)v[i]=temp1$ci[2,2]/temp1$ci[2,6]
}
ibot<-round(alpha*nboot/2)
itop<-nboot-ibot+1
ibot=ibot+1
vs=sort(v[1:nboot])
crit=c(vs[ibot],vs[itop])
test=v[Nboot]
low=r1-vs[itop]*temp1$ci[2,6]
up=r1-vs[ibot]*temp1$ci[2,6]
pv=rhohc4btv2(X1,Y1,nboot=nboot,SEED=SEED)
list(est=r1,test=test,crit.val=crit,p.value=pv,ci=c(low,up))
}


rhohc4btv2<-function(X1,Y1,alpha=.05,nboot=2999,SEED=TRUE){
#
# A p-value for rhohc4bt
# Pearson's correlations using a bootstrap-t method in conjunction with the HC4 estimator
#
if(SEED)set.seed(2)
XY=cbind(X1,Y1)
XY=elimna(XY)
X1=XY[,1]
Y1=XY[,2]
r1=cor(X1,Y1)
n1=length(X1)
v=NA
alph=seq(0.01,0.99,.01)
for(i in 1:nboot){
id1=sample(n1,n1,replace=TRUE)
x1=X1[id1]
y1=Y1[id1]
x1=(x1-mean(x1))/sd(x1)
y1=(y1-mean(y1))/sd(y1)
temp1=olshc4(x1,y1)
v[i]=(temp1$ci[2,2]-r1)/temp1$ci[2,6]
}
vs=sort(v)
na=length(alph)
temp1=olshc4(X1,Y1)
for(i in 1:na){
ibot<-round(alph[i]*nboot/2)
itop<-nboot-ibot+1
ibot=ibot+1
low=r1-abs(vs[itop])*temp1$ci[2,6]
up=r1+abs(vs[ibot])*temp1$ci[2,6]
if(sign(low*up)==1)break
}
alph[i]
}

 Dmul.loc2g<-function(m1,m2=NULL,nullv=rep(0,ncol(m1)),locfun=smean,alpha=.05,SEED=TRUE,
nboot=500,...){
#
#  Inferences about a multivariate measure of location. A percentile bootstrap method
#  is used for each of the p variables.
#
#  By default, a skipped estimator is used where outliers are identified and removed via projection method.
#  For a large number of variables, say > 8, might include the argument
#  op=3 and
#  outfun=outproadj
