}                                                                                                                             
                                                                                                                              
anc.best.fun<-function(a,init,iter,rem,Jm1,alpha){                                                                            
#                                                                                                                             
chk=0                                                                                                                         
init=a*init                                                                                                                   
for(i in 1:iter){                                                                                                             
