cell.com.pv<-function(x,i=1,j=2,method='AC'){
#
# For a multinomial distribution, compute a confidence interval
#  for p_i-p_j, the difference between the probabilities asscoiated with cells i and j
#
# x= cell frequencies
#
n=sum(x)
p1=x[i]/n
p2=x[j]/n
COR=0-sqrt(p1*p1/((1-p1)*(1-p2)))
a=seq(.001,.1,.001)
a=c(a,seq(.1,.99,.01))
a=rev(a)

if(x[i]==x[j])pv=1
if(x[i]!=x[j]){
for(k in 1:length(a)){
c2=acbinomci(x[j],n,alpha=a[k])$ci
c1=acbinomci(x[i],n,alpha=a[k])$ci
T1=(p1-c1[1])^2+(p2-c2[2])^2-2*COR*(p1-c1[1])*(c2[2]-p2)
T2=(p1-c1[2])^2+(p2-c2[1])^2-2*COR*(c1[2]-p1)*(p2-c2[1])
T2=max(c(0,T2))
T1=max(c(0,T1))
L=p1-p2-sqrt(T1)
U=p1-p2+sqrt(T2)
pv=a[k]
if(sign(L*U)<0)break
}}
if(n<=35){
if(x[i]==x[j])pvnew=1
else{
pv.up=pv+.1
anew=seq(pv,pv.up,.01)
for(k in 1:length(anew)){
c1=binom.conf(x[i],n,AUTO=TRUE,method=method,alpha=anew[k])$ci
c2=binom.conf(x[j],n,AUTO=TRUE,method=method,alpha=anew[k])$ci
T1=(p1-c1[1])^2+(p2-c2[2])^2-2*COR*(p1-c1[1])*(c2[2]-p2)
T2=(p1-c1[2])^2+(p2-c2[1])^2-2*COR*(c1[2]-p1)*(p2-c2[1])
T2=max(c(0,T2))
T1=max(c(0,T1))
L=p1-p2-sqrt(T1)
U=p1-p2+sqrt(T2)
pvnew=anew[k]
if(sign(L*U)>0)break
}}
pv=pvnew
}
pv
}


cell.com<-function(x,i,j,alpha=.05,AUTO=TRUE,method='AC'){
#
# For a multinomial distribution, compuate a confidence interval
#  for p_i-p_j, the difference between the probabilities asscoiated with cells i and j
#
# x= cell frequencies
#
n=sum(x)
c1=binom.conf(x[i],n,AUTO=AUTO,method=method,alpha=alpha)$ci                                            
c2=binom.conf(x[j],n,AUTO=AUTO,method=method,alpha=alpha)$ci
p1=x[i]/n
p2=x[j]/n
COR=0-sqrt(p1*p1/((1-p1)*(1-p2)))
T1=(p1-c1[1])^2+(p2-c2[2])^2-2*COR*(p1-c1[1])*(c2[2]-p2)
T2=(p1-c1[2])^2+(p2-c2[1])^2-2*COR*(c1[2]-p1)*(p2-c2[1])
T2=max(c(0,T2))
T1=max(c(0,T1))
L=p1-p2-sqrt(T1)
U=p1-p2+sqrt(T2)
list(ci=c(L,U))
}

