zwe<-function(x,k=3,C=0.2){
#
#  Zuo's  (2010) weighted estimator
#
x=elimna(x)
SD=abs((x-median(x)))/mad(x,constant=1)
D=1/(SD+1)
n=length(x)
IDGE =rep(0,n)
flag=D >= C
IDGE[flag]=1
IDLT=rep(0,n)
flag=D<C
IDLT[flag]=1
top=exp(-1*k*(1-D^2/C^2)^2) - exp(-1*k)*IDLT
bot=(1-exp(-1*k))
w=IDGE+top/bot
e=sum(w*x)/sum(w)
e
}

